function myFunction() {
  let spreadsheet = SpreadsheetApp.openByUrl('https://docs.google.com/spreadsheets/d/1BwlenLRfmUWrdwUF6irWkHQgkHKpfichdfkYvmRD2pM/edit#gid=1063505565')
  let values = spreadsheet.getRange('A1:A5').getValues();
  let form = FormApp.create("Favorite Author");
  let item = form.addMultipleChoiceItem();
  item.setTitle("Which is Your Favorite Authore? "); 
  let choices = [];
  for (let row of values){
    choices.push(item.createChoice(row[0]));
  }
  item.setChoices(choices);

  var email_list = SpreadsheetApp.openByUrl('https://docs.google.com/spreadsheets/d/1YwzWi2eahR4gj44981HWng4W1UtqHse4oBqBDX0vUSg/edit#gid=0');
  var emails =email_list.getRange('A1:A9').getValues();
    emails.forEach(function (row)
     {
      var email = row[0];
      MailApp.sendEmail(email,"Best Authors Survey 2021 ", "Please fill up the following form and help us to know, who is the most faivorite author's of the year.\n Here is the form: \n  "+ form.getPublishedUrl());
     });

}
